sections.css:
.title-super {
	font-family: $title-super-font;
	font-size: $title-super-font-size;
	line-height: $title-super-line-height;
	color: $title-super-font-color;
}
.h1, h1 {
	font-family: $h1-font;
	color: $h1-font-color;
	font-size: $h1-font-size;
	line-height:$h1-line-height;
}
.h2, h2 {
	font-family: $h2-font;
	color: $h2-font-color;
	font-size: $h2-font-size;
	line-height:$h2-line-height;
}
.h3, h3 {
	font-family: $h3-font;
	color: $h3-font-color;
	font-size: $h3-font-size;
	line-height:$h3-line-height;
}
.h4, h4 {
	font-family: $h4-font;
	color: $h4-font-color;
	font-size: $h4-font-size;
	line-height:$h4-line-height;
}
.h5, h5 {
	font-family: $h5-font;
	color: $h5-font-color;
	font-size: $h5-font-size;
	line-height:$h5-line-height;
}
.h6, h6 {
	font-family: $h6-font;
	color: $h6-font-color;
	font-size: $h6-font-size;
	line-height:$h6-line-height;
	text-transform:uppercase;
}

.normal, body {
	font-family: $default-font;
	color: $default-font-color;
}

.paraintro {
	font-family: $paraintro-font;
	font-size: $paraintro-font-size;
	line-height: $paraintro-line-height;
	margin: $paraintro-margin;
	color: $paraintro-font-color;
}

.paranormal {
	font-family: $paranormal-font;
	font-size: $paranormal-font-size;
	line-height: $paranormal-line-height;
	margin: $paranormal-margin;
	color: $paranormal-font-color;
}
.parasmall {
	font-family: $parasmall-font;
	font-size: $parasmall-font-size;
	line-height: $parasmall-line-height;
	margin: $parasmall-margin;
	color: $parasmall-font-color;
}

a, .link {
	color:$color_link;
	text-decoration:$link-text-decoration;

	&:visited, &.visited {
		color: $color_link_visited;
		text-decoration:$link-visited-text-decoration;
	}
	&:active, &.active{
		color: $color_link_active;
		text-decoration:$link-avtive-text-decoration;
	}
	&:hover, &.hover {
		color: $color_link_hover;
		text-decoration:$link-hover-text-decoration;
	}
}

h1, .h1, h2, .h2, h3, .h3, h4, .h4, h5, .h5, h6, .h6, .title-super {
	& a {
		text-decoration:none;
		color:inherit;
	}
}

.price {
	color: $price-color;
	font-family:$price-font-family;
	display:table;

	span {
		display:table-cell;
	}

	.currency-symbol {
		font-size: $currency-symbol-font-size;
	}

	&.discount {
		color: $price-discounted-color;
	}
	&.original {
		color: $price-original-color;
		text-decoration: line-through;
		font-size:$price-original-font-size;
	}

	.price {
		padding-left:0.5em;
	}
}

.badge {
	height: $badge-height;
	float:left;
	position:absolute;

	.badge-text {
		line-height: $badge-line-height;
		font-family: $badge-font-family;
		font-size: $badge-font-size;
		padding: $badge-padding;
		text-transform: $badge-text-transform;
	}

	&:after {
		border-top: $badge-after-border-top;
		border-bottom: $badge-after-border-bottom;
		border-left: $badge-after-border-left;
		content:' ';
		display: block;
		position:absolute;
		top: 0;
		right: $badge-after-right;
	}

	&.preorder {
		background: $badge-preorder-background;
		&:after {
			border-left-color: $badge-preorder-background;
		}
	}
	&.preview {
		background: $badge-preview-background;
		&:after {
			border-left-color: $badge-preview-background;
		}
	}
	&.soldout {
		background: $badge-soldout-background;
		&:after {
			border-left-color: $badge-soldout-background;
		}
	}
	&.exclusive {
		background: $badge-exclusive-background;
		&:after {
			border-left-color: $badge-exclusive-background;
		}
	}
	&.promotion {
		background: $badge-promotion-background;
		&:after {
			border-left-color: $badge-promotion-background;
		}
	}
	&.sale {
		background-color: $badge-sale-background;
		&:after {
			border-left-color: $badge-sale-background;
		}
	}
	&.custom {
		background-color: $badge-custom-background;
		&:after {
			border-left-color: $badge-custom-background;
		}
	}
	&.new {
		background: $badge-new-background;
		&:after {
			border-left-color: $badge-new-background;
		}
	}
}

.text-input {
	@include text-input(200px);
}
::placeholder { 
    color:    $input_placeholder_color;
    text-transform: uppercase;
}
.form-table {
	display:table;
	width:100%;
}
.form-row {
	display:table-row;
}
.form-cell {
	position:relative;
	display:table-cell;
	margin-bottom:20px;
}

select.bs-select-hidden,
select.selectpicker {
  display: none;
}

.bootstrap-select {
	position:relative;
	font-family: $default-font;
	font-size: $select-font-size;
}
.mobile-device {
    display: block !important;
    position: absolute;
    opacity: 0;
    z-index: $zindex-select;
    top: 0;
  	height: $select-height;
  	width: 100%;
  	-webkit-appearance: menulist-button;
}

.bootstrap-select button{
	padding: $select-padding;
	border: $select-border;
	border-radius: $select-border-radius;
	line-height: $select-line-height;
	background-color:$select-background-color;
	color:$select-color;
	position:relative;
	min-width:100%;
	width:100%;
}
.bootstrap-select.open button {
	border-bottom:0px;
	z-index: $zindex-select;
}
.bootstrap-select button .caret {
	border:none;
}
.bootstrap-select button .caret:after {
	color: $select-caret-color;
	content: $select-cart-icon;
	position: absolute;
	top: $select-caret-top;
	right: $select-caret-right;
	font-family: $icomoon;
	speak: none;
	-webkit-font-smoothing: antialiased;
}
.bootstrap-select.open button .caret:after {
	transform:rotate(180deg);
}

//Copied from bootstrap _drowdown:
// The dropdown wrapper (div)
.dropup,
.dropdown {
  position: relative;
}

// Prevent the focus on the dropdown toggle when closing dropdowns
.dropdown-toggle:focus {
  outline: 0;
}

.bootstrap-select .dropdown-menu {
	position: relative;
    top: 100%;
    left: 0;
    z-index: 1000;
    display: none;
    float: none;
    min-width: 160px;
    padding: 0;
    margin: 0;
    list-style: none;
    font-size: 1em;
    text-align: left;
    background-color: #fff;
    border: none;
}

// The dropdown menu (ul)
.bootstrap-select .dropdown-menu.open {
  position: absolute;
  left: 0;
  z-index: 1000;
  display: none; // none by default, but block on "open" of the menu
  list-style: none;
  text-align: left; // Ensures proper alignment if parent has it changed (e.g., modal footer)
  background-color: $select_dropdown_background;
  border-top: $select_option_border;
  min-width: 100%;
  width:100%;
}
.bootstrap-select .dropdown-menu.inner {
	list-style:none;
	padding:0px;
	margin:0;
	& li a{
		cursor: pointer;
		padding: $select-option-padding;
		background-color: $select_dropdown_background;
		border: $input_border;
		border-top: none;
		display:block;
		outline:0;
		color:$select-option-color;

		&:hover,&:focus {
			border-bottom:solid 2px $select_dropdown_selected;
		  	color: $select_dropdown_selected;
		  	outline:0;
		  	padding:$select-option-hover-padding;
		}
	}

	& li.dropdown-header {
		padding: $select-option-padding;
		background-color: #fff;
		border: $select-option-border;
		border-top: none;
		font-style: italic;
		font-family: $select-option-header-font;
	}
}
.bootstrap-select.open {
  // Show the menu
  > .dropdown-menu {
    display: block;
  }

  // Remove the outline when :focus is triggered
  > a {
    outline: 0;
  }
}

.select-size {
	width:237px;

	&.open .dropdown-toggle {
		border-bottom: solid 1px #FFF;
	}
	& .dropdown-menu.open {
		border: $input_border;
		width: 311px;
		top: $select-height;
		overflow:visible !important;
	}
	& .dropdown-menu.inner {
		padding: $select-size-inner-padding;
	}
	& .dropdown-menu.inner li {
		display:inline;
		float:left;
		margin: $select-size-option-margin;
		min-width: $select-size-option-min-width;
		text-align: center;
		& a {
			padding:0px;
			border-top: $input_border;
			border-radius: $select-border-radius;

			&:hover, &:focus {
				border:solid 1px $select_dropdown_selected;
		  		border-bottom:$select-option-hover-border;
		  		color: $select_dropdown_selected;
		  		outline:0;

		  		& span:first-child {
		  			padding:$select-size-option-hvr-padding;
		  		}
			}
			& span:first-child {
		  		padding: $select-size-option-padding;
		  		display: block;
		  	}

		}
		&.dropdown-header {
			display: block;
			width: 100%;
			border: none;
			margin: $select-size-option-hdr-margin;
			padding:0px;
		}
	}
}

header {
	width:100%;
	height: 48px;
	background-color:#000;
	position:fixed;
	z-index:$zindex-header;
}
nav {
	width:950px;
	margin:0 auto;
	ul {
		list-style: none;
		margin:0;
		padding:0;
	}
	> ul > li {
		display:inline-block;
	}

	li.nav-item {
		line-height: 42px;
	    height: 45px;
	    text-align: center;
	    cursor: pointer;
	    color: #fff;
	    padding: 1px 5px 0;
	    letter-spacing: 1px;
	    vertical-align: top;
	    margin-top:3px;
	    font-family: $adiNeueBold;
	    font-size: 0.938em;

	    &:hover, .active {
	    	background-color:#FFF;
	    	color:#000;

	    	.subnav {
	    		top:48px;
	    	}
	    }
	}

	li.divider {
    	width:1px;
    	margin:12px 5px;
    	background-color:rgba(255,255,255,0.25);
    	height:24px;
	}

	li.logo  {
		margin-right:20px;
		a{
			background:url(data:image/png;base64,) no-repeat 0 13px;
			width: 156px;
			height:48px;
			display: block;
		}
	}
	li.cart, li.account {
		line-height: 42px;
	    height: 45px;
	    margin-top: 3px;
	    vertical-align: top;
	    padding: 0 6px;
	    cursor: pointer;
	    color:#FFF;

	    &:hover, .active {
	    	background-color:#FFF;
	    	color:#000;
	    }
	}
	li.cart:after {
		font-family: $icomoon;
		content: "\e632";
		font-size: 1.5em;
	}

	li.account:after {
		font-family: $icomoon;
		content: "\e019";
		font-size:1.5em;
	}
	li.search {
		vertical-align: top;
		a {
	        background: #fff;
		    color: #000;
		    cursor: pointer;
		    display: inline-block;
		    min-width: 96px;
		    height: 32px;
		    line-height: 33px;
		    padding: 0 6px 0 32px;
		    text-align: center;
		    margin: 8px;
			position: relative;
			font-family: $default-font;
			font-size:0.813em;

			&:hover {
				background-color: $color_link;
				color:#FFF;
			}

		    &:before {
		    	width: 20px;
			    height: 18px;
			    display: block;
			    content: "\e625";
			    position: absolute;
			    left: 8px;
			    top: 6px;
			    font-family: $icomoon;
			    speak: none;
			    font-size: 20px;
			    line-height: 1;
		    }
		}
	}
	.subnav {
		background-color:#FFF;
		transition-delay: 0.15s;
		@include reset-text();
		position: absolute;
		width: 100%;
		left:0;
		font-size: 16px;
		top: -5000em;

		ul li a, .bottom-line a {
			font-size: $font-size-base;
			color: $default-font-color;
			line-height: $line-height-base;
		}
		.bottom-line {
			clear:both;
			margin-bottom: 20px;

			.col-5:before {
			    content: " ";
			    display: block;
			    height: 1px;
			    width: 100%;
			    overflow: hidden;
			    background: #ccc;
			    margin: 0 0 10px;
			}
		}
		h4 {
			margin:1em 0 0.5em;
		}
		&:after {
		    content: " ";
		    display: block;
		    width: 100%;
		    height: 7px;
		    overflow: hidden;
		    background: rgba(0,0,0,0.25);
		    border-width: 0 0 1px 0;
		    border-style: solid;
		    border-color: rgba(0,0,0,0.5);
		}
	}
}

